INSERT INTO `subject` (`subID`, `subCredit`, `subName`, `semester`, `year`, `semesterrequired`, `condition1`, `condition2`, `andOr`, `passCondition`, `subjDescription`) VALUES
('BA291', 3, 'ธุรกิจเบื้องต้น', 2, 1, 2, NULL, NULL, NULL, 'F', 'ลักษณะของธุรกิจสภาพแวดล้อมและรูปแบบความเป็นเจ้าของธุรกิจ การบริหารธุรกิจกิจกรรมทางธุรกิจด้านการผลิตการตลาดการเงินการบัญชีการบริหารสารสนเทศ และการบริหารทรัพยากรมนุษย์ ทั้งนี้เพื่อปูพื้นฐานแนวความคิดของการบริหารธุรกิจ และให้เกิดความคิดรวบยอดผ่านการจัดทำแผนธุรกิจ หมายเหตุ เป็นวิชาสำหรับนักศึกษานอกคณะพาณิชย์ฯที่ประสงค์จะเรียนรายวิชาต่าง ๆ ของคณะฯเป็นวิชาโทควรจะเรียนวิชา พบ.291 ก่อนวิชาอื่นเพื่อใช้เป็นพื้นฐานในการเรียนวิชาต่างๆของคณะพาณิชย์ฯ'),
('CS101', 3, 'โครงสร้างแบบไม่ต่อเนื่อง', 1, 1, 1, NULL, NULL, NULL, 'D+', 'ตรรกศาสตร์เบื้องต้นและการประยุกต์ใช้ในวงจรดิจิทัลพื้นฐาน วิธีการพิสูจน์ เซต ฟังก์ชัน ความสัมพันธ์ พีชคณิตบูลีน พื้นฐานการนับ และ กราฟ โดยเน้นการประยุกต์ใช้ในด้านวิทยาการคอมพิวเตอร์'),
('CS102', 3, 'พื้นฐานการแก้ปัญหาและการโปรแกรมคอมพิวเตอร์', 1, 1, 1, NULL, NULL, NULL, 'D+', 'การแก้ปัญหาด้วยวิธีการทางคอมพิวเตอร์ การออกแบบและการโปรแกรมเชิงโครงสร้างชนิดของข้อมูล โครงสร้างการควบคุม ฟังก์ชันและการส่งค่าพารามิเตอร์ ฟังก์ชันเวียนเกิด การทดสอบ และตรวจหาข้อผิดพลาด'),
('CS111', 3, 'แนวคิดเชิงวัตถุ', 2, 1, 2, 'pass_CS101', 'pass_CS102', 'and', 'D+', 'พัฒนาทักษะการวิเคราะห์และการแก้ปัญหาด้วยแนวคิดเชิงวัตถุ บูรณาการแนวคิดและแนวทาง เชิงปฎิบัติที่ทันสมัยในวัฏจักรการพัฒนาซอฟต์แวร์ หลักการพื้นฐานในการเขียนโปรแกรมเชิงวัตถุตามที่วิเคราะห์และออกแบบไว้ แนวทางในการทวนสอบความสอดคล้องระหว่างแบบและซอร์สโค้ด'),
('CS213', 3, 'โครงสร้างข้อมูล', 1, 2, 4, 'ever_CS111', 'ever_CS112', 'or', 'F', 'การวิเคราะห์ขั้นตอนวิธีเบื้องต้น หลักนามธรรมข้อมูล โครงสร้างข้อมูลพื้นฐานได้แก่ ข้อมูลเชิงเส้นและข้อมูลแบบไม่เชิงเส้น การจัดสรรหน่วยเก็บแบบพลวัต เทคนิคการค้นหา และการเรียงลำดับข้อมูลในหน่วยความจำ การฝึกปฏิบัตินอกเวลา'),
('CS216', 3, 'โครงสร้างข้อมูลและขั้นตอนวิธี', 1, 2, 4, 'ever_CS111', 'ever_CS112', 'or', 'F', 'การวิเคราะห์เวลาดำเนินงานและความซับซ้อนของขั้นตอนวิธี โครงสร้างข้อมูลแบบต่าง ๆ อาร์เรย์ ลิงค์ลิสต์ สแตค คิว ทรี แฮช กราฟ ไบนารีฮีป การวิเคราะห์ความต้องการด้านเวลา และ หน่วยความจำใน การค้นหา ขั้นตอนวิธีเกี่ยวกับการค้นหาและการเรียงลำดับ การแก้ปัญหาแบบบรูทฟอร์ซ การแบ่งแล้วเข้ายึด ขั้นตอนวิธีการแบบละโมบ การชดเชยกันระหว่างเวลากับเนื้อที่ของการคำนวณ'),
('CS217', 3, 'การออกแบบและวิเคราะห์ขั้นตอนวิธี', 2, 2, 5, NULL, NULL, NULL, 'F', 'เทคนิคต่างๆ ที่ใช้ในการออกแบบและวิเคราะห์ขั้นตอนวิธี ได้แก่ วิธีการแบ่งแยกเอาชนะ วิธีละโมบ วิธีกำหนดการพลวัต วิธีการแวะผ่านบัพทุกบัพของกราฟ วิธีการย้อนรอย และวิธีบรานช์แอนบาวด์ ตัวอย่างการใช้งานครอบคลุมวิธีการเรียงลำดับ และการค้นหาข้อมูล การแปลงฟูเรียร์ ขั้นตอนวิธีแบบสุ่ม ขั้นตอนวิธีต่างๆ สำหรับกราฟ ขั้นตอนการหาค่าที่ให้ประโยชน์สูงสุด'),
('CS221', 3, 'โครงสร้างและระบบคอมพิวเตอร์เบื้องต้น', 1, 2, 4, 'ever_CS101', 'ever_CS102', 'and', 'F', 'สาระสำคัญเกี่ยวกับโครงสร้างและการอิมพลีเมนต์ดิจิทัลคอมพิวเตอร์ กระบวนการบูตเครื่องคอมพิวเตอร์ ระบบแทนข้อมูลและการจัดการข้อมูลแบบครบหน่วย ข้อผิดพลาดเกี่ยวกับการจัดเก็บและประมวลผลเลขทศนิยม ชุดคำสั่ง การแปลและประมวลผลโปรแกรม ลำดับขั้นของหน่วยความจำในระบบคอมพิวเตอร์ พื้นฐานฮาร์ดแวร์ของหน่วยประมวลผล ได้แก่ วงจรรวม วงจรซีเควนเชียล และ แนวทางการสร้างหน่วยประมวลผลแบบง่าย การต่อเชื่อมกับอุปกรณ์ภายนอก'),
('CS222', 3, 'ระบบปฏิบัติการ&nbsp;1', 2, 2, 5, 'ever_CS213', 'ever_CS211', 'and', 'F', 'พื้นฐานการออกแบบและการสร้างระบบปฏิบัติการ องค์ประกอบของระบบปฏิบัติการ การครอบครองเดี่ยว สมวาร โปรเซสและการจัดการ การจัดการหน่วยความจำ ระบบไฟล์ การจัดการอุปกรณ์ระบบความปลอดภัย กรณีศึกษา'),
('CS233', 3, 'สถาปัตยกรรมคอมพิวเตอร์และระบบปฎิบัติการ', 1, 2, 4, 'ever_101', 'ever_CS102', 'and', 'F', 'สถาปัตยกรรมชุดคำสั่ง การประมวลผลแบบสายท่อ มัลติโพรเซสเซอร์ การจัดการหน่วยความจำ แคชและการ จัดระบบหน่วยความจำเสมือน การประสานเวลาของโปรเซส การจัดตารางซีพียู มัลติเธรดดิ้งระบบไฟล์ การควบคุมอินพุตเอาท์พุตและอุปกรณ์'),
('CS234', 3, 'ระบบเครือข่ายคอมพิวเตอร์และความปลอดภัย', 2, 2, 5, 'ever_CS233', NULL, NULL, 'F', 'ระบบเครือข่ายอินเทอร์เน็ต รวมถึงความปลอดภัยของสารสนเทศ ลายมือชื่อดิจิทัล ใบรับรองดิจิทัล โปรแกรมที่มีจุดประสงค์ร้าย นโยบายและแบบจำลองความปลอดภัย การรับรองความปลอดภัย การวิเคราะห์ความเสี่ยง การจัดการความปลอดภัยของระบบสารสนเทศ'),
('CS241', 3, 'ภาษาโปรแกรมและกรอบความคิด', 2, 2, 5, 'ever_CS111', 'ever_CS112', 'or', 'F', 'หลักการของภาษาโปรแกรม ไวยกรณ์และวากยะสัมพันธ์ ศึกษากรอบแนวคิดของภาษาโปรแกรมประเภทต่างๆ การโปรแกรมโดยคำนึงถึงสมรรถนะ การโปรแกรมโดยอาศัยส่วนต่อประสานโปรแกรมประยุกต์ ไลบรารี่สำหรับจัดการข้อมูล และการโปรแกรมเชิงเหตุการณ์'),
('CS245', 3, 'หลักการวิทยาการข้อมูล', 2, 2, 5, 'ever_ST216', 'ever_ST211', 'or', 'F', 'หลักการพื้นฐานของวิทยาการข้อมูลที่เกี่ยวข้งกับอัลกอริทึม กระบวนการ วิธีการและหลักการคิด รวมทั้งเครื่องมือที่ใช้ในการวิเคราะห์ข้อมูล หัวข้อพื้นฐานในวิทยาการข้อมูล ได้แก่ การจัดการข้อมูล การวิเคราะห์ข้อมูลเชิงสถิติ และเชิงการเรียนรู้ของเครื่องจักร การนำเสนอข้อมูลด้วยทัศนสารสนเทศและการทำงานกับข้อมูลขนาดใหญ่'),
('CS246', 3, 'การแสดงข้อมูล', 2, 2, 5, 'ever_CS245', NULL, NULL, 'F', 'หลักการออกแบบ พัฒนาและการประเมินการแสดงข้อมูล การได้มา การแยก และการวิเคราะห์ ข้อมูลขนาดใหญ่ เทคนิคและเครื่องมือสำหรับข้อมูลรูปแบบต่างๆ เพื่อสื่อสารสิ่งที่ค้นพบหรือคำตอบของปัญหา สำหรับสนับสนุนการตัดสินใจ'),
('CS251', 3, 'ระบบฐานข้อมูล&nbsp;1', 2, 2, 5, 'ever_CS213', 'ever_CS216', 'or', 'F', 'แนวคิดพื้นฐานและสถาปัตยกรรมของระบบฐานข้อมูล การจำลองข้อมูลเชิงแนวคิด การออกแบบฐานข้อมูลและการทำให้เกิดผล ภาษาสอบถาม เมตาเดตา การจัดระเบียบแฟ้มและหน่วยเก็บข้อมูลเชิงกายภาพ แนะน าการจัดการรายการเปลี่ยนแปลง มีฝึกปฏิบัตินอกเวลา'),
('CS253', 3, 'การเขียนโปรแกรมสำหรับธุรกิจ', NULL, NULL, NULL, 'ever_CS102', 'ever_CS103', 'or', 'F', 'การแก้ปัญหาทางธุรกิจแบบเบื้องต้นด้วยวิธีการทางคอมพิวเตอร์ การออกแบบและพัฒนา อัลกอริทึมด้วยเทคนิคและเครื่องมือทางคอมพิวเตอร์ที่เหมาะสมกับปัญหาทางธุรกิจ การทดสอบ และ ตรวจหาข้อผิดพลาด การพัฒนาส่วนต่อประสานผู้ใช้เชิงกราฟิกเบื้องต้น การพัฒนาโปรแกรมเชิงเหตุการณ'),
('CS254', 3, 'การเขียนโปรแกรมด้วยภาษาไพทอน', NULL, NULL, NULL, 'ever_CS111', NULL, NULL, 'F', 'การแก้ปัญหาด้วยวิธีการทางคอมพิวเตอร์ด้วยภาษาไพทอน การโปรแกรมเชิงโครงสร้าง ชนิดของ ข้อมูลพื้นฐาน โครงสร้างการควบคุม ฟังก์ชัน ฟังก์ชันเวียนเกิด โครงสร้างข้อมูลพื้นฐาน ลิสต์ ทูเพิล เซ็ต ดิกชันนารี เรนจ์ การทดสอบ ตรวจหาข้อผิดพลาด การโปรแกรมเชิงวัตถุด้วยภาษาไพทอน คลาส เมธอด ไลบราลีภาษาไพทอนส าหรับการประมวลผลเชิงตัวเลข อาร์เรย์และแมทริกซ์ไลบราลีภาษาไพทอนส าหรับ แสดงข้อมูล พื้นฐานการใช้งานไลบราลีภาษาไพทอนส าหรับการวิเคราะห์ข้อมูล'),
('CS262', 3, 'การทดสอบซอฟต์แวร์เบื้องต้น', 1, 2, 4, 'ever_CS111', NULL, NULL, 'F', 'แนวคิดการทดสอบและการประกันคุณภาพซอฟต์แวร์ เทคนิคการสร้างข้อมูลการทดสอบแบบแบลคบอกซ์และไวท์บอกซ์ การทดสอบซอฟต์แวร์ในระดับยูนิต โมดูล ระบบย่อย และทั้งระบบกระบวนการทดสอบและการวางแผนการทดสอบซอฟต์แวร์'),
('CS264', 3, 'วิศวกรรมซอฟต์แวร์เบื้องต้น', 1, 2, 4, 'ever_CS111', 'ever_CS112', 'or', 'F', 'คุณลักษณะซอฟต์แวร์ คุณภาพของซอฟต์แวร์และโครงการซอฟต์แวร์ หลักการของวิศวกรรมซอฟต์แวร์ ระเบียบวิธีในการพัฒนาซอฟต์แวร์ กระบวนการพัฒนาซอฟต์แวร์ที่สำคัญ การวางแผนและจัดการโครงการ การประเมินเวลาและค่าใช้จ่าย วิเคราะห์และการระบุข้อกำหนด เทคนิคการวิเคราะห์และออกแบบซอฟต์แวร์ แนะนำสถาปัตยกรรมซอฟต์แวร์ เทคนิคการพัฒนาโปรแกรม การตรวจสอบและการทวนสอบซอฟต์แวร์ วิวัฒนาการของซอฟต์แวร์ สภาพแวดล้อมการผลิตซอฟต์แวร์และเครื่องมือช่วยจัดการโครงการ กรณีศึกษา มีฝึกปฏิบัตินอกเวลา'),
('CS265', 3, 'ปัญญาประดิษฐ์เบื้องต้น', 1, 3, 7, 'ever_CS213', 'ever_CS216', 'or', 'F', 'ปัญหาทางปัญญาประดิษฐ์ การแทนความรู้ การค้นหาแบบไม่มีข้อมูล การค้นหาแบบมีข้อมูล ปัญหาการทำตามเงื่อนไข ตรรกศาสตร์และการให้เหตุผลอัตโนมัติ ทฤษฎีเกม การเรียนรู้ของเครื่องจักร เบื้องต้น'),
('CS266', 3, 'กระบวนการซอฟต์แวร์และการประกันคุณภาพ', 1, 3, 7, 'ever_CS264', NULL, NULL, 'F', 'องค์ประกอบของกระบวนการซอฟต์แวร์ครอบคลุม กิจกรรม วิธีการ และวิธีปฏิบัติที่ใช้ เพื่อพัฒนาและบำรุงรักษาซอฟต์แวร์ รวมถึงชิ้นงานอื่นๆที่เกี่ยวข้องกับการพัฒนาซอฟต์แวร์ แนะนำหลักการเบื้องต้นของการประกันคุณภาพของกระบวนการพัฒนาซอฟต์แวร์ และตัวซอฟต์แวร์ แนะนำการใช้ร่วมกับความรู้เบื้องต้นในการนิยามสำหรับตัววัด และการวัด'),
('CS267', 3, 'การกำหนดและการจัดการความต้องการทางซอฟต์แวร์', 2, 2, 5, 'ever_CS264', NULL, NULL, 'F', 'กระบวนการในการหาความต้องการในหลายรูปแบบได้แก่ การสืบค้น การวิเคราะห์ การหาข้อตกลง การระบุข้อกำหนด การทวนสอบ และหลักการเบื้องต้นของการบริหารจัดการความต้องการศึกษาระเบียบวิธี แนะนำเทคนิคและเครื่องมือที่จะนำไปใช้เพื่อนิยาม สร้างเอกสาร และประกันความพึงพอใจของลูกค้า'),
('CS285', 3, 'การประมวลสารสนเทศของมนุษย', NULL, NULL, NULL, NULL, NULL, NULL, 'F', 'งานวิจัยและทฤษฎีเบื้องต้นเกี่ยวกับการประมวลผลสารสนเทศของมนุษย์ ความใส่ใจ การรู้จ าแบบ ความจ า การแทนองค์ความรู้และภาษา กระบวนการแก้ไขปัญหา กระบวนการหาเหตุผล และกระบวนการ เรียนรู้ ความสัมพันธ์ระหว่างกระบวนการรู้จ าของมนุษย์กับต้นแบบทางคอมพิวเตอร์ และการประมวล นัยส าคัญของเนื้อหาเกี่ยวกับการรู้จ าเพื่อสร้างระบบสารสนเทศ'),
('CS299', 3, 'คอมพิวเตอร์กราฟิก&nbsp;1', 1, 2, 4, 'ever_CS111', NULL, NULL, 'F', 'ซอฟต์แวร์และฮาร์ดแวร์ สำหรับคอมพิวเตอร์กราฟิกแบบโต้ตอบ วิธีการทั่วไปที่ใช้ในการออกแบบและแสดงผลภาพ การดำเนินการพื้นฐานในปริภูมิ 2 มิติและ 3 มิติ การแปลงในระบบเรขาคณิต การแปลงจุดหรือตำแหน่งที่มองเห็น การตัดภาพ ทฤษฎีสี มีฝึกปฏิบัตินอกเวลา'),
('CS300', 1, 'สัมมนาการโปรแกรมภาคปฏิบัติ', 1, 2, 4, 'pass_CS111', NULL, NULL, 'U', 'สัมมนา การออกแบบและการโปรแกรมคอมพิวเตอร์เชิงโครงสร้างและเชิงวัตถุเพื่อการแก้ไขปัญหาให้ปฏิบัติได้จริงสำหรับปัญหาพิเศษทางวิทยาการคอมพิวเตอร์ การวัดผลแบ่งเป็น 2 ระดับ คือ ระดับ S(ใช้ได้) และ ระดับ U (ยังใช้ไม่ได้)'),
